INSERT INTO `faq_translates` (`id`, `question`, `answer`, `faq_id`, `lang_key`, `created_at`, `updated_at`) VALUES
(25, 'Paketim necə göndərildi?', 'Antioksidantla zəngin supermeyvələr, vitaminlər və dərini qidalandıran digər qidalarla zənginləşdirilmiş dərini mükəmməlləşdirən təbii tonal krem. Yumşaq, məxmər kimi hamar dəri üçün təmiz tutqun bitişli kremli maye formula dəstləri. Dərinizə əriyən 7 çalarda gələn qüsursuz, qalıcı tonal kremə salam deyin. Bu yüngül, innovativ formula cizgilərə yerləşməyən hamar, təbii tutqun bitirmə yaradır. Dərinizə mükəmməl uyğun gəlir. 1 fl. oz.', 9, 'az', '2024-06-08 13:34:19', '2024-06-08 13:34:19'),
(26, 'How did my package ship?', '<div id=\"flush-collapseOne\" class=\"accordion-collapse collapse show\" aria-labelledby=\"flush-headingOne\" data-bs-parent=\"#accordionFlushExample\" style=\"\">Complexion-perfecting natural\r\n												foundation enriched with\r\n												antioxidant-packed superfruits,\r\n												vitamins, and other\r\n												skin-nourishing nutrients.\r\n												Creamy liquid formula sets with\r\n												a pristine matte finish for\r\n												soft, velvety smooth skin.Say hello to flawless,\r\n												long-lasting foundation that\r\n												comes in 7 melt-into-your-skin\r\n												shades. This lightweight,\r\n												innovative formula creates a\r\n												smooth, natural matte finish\r\n												that won’t settle into lines.\r\n												It’s the perfect fit for your\r\n												skin. 1 fl. oz.\r\n											<div class=\"py-8\">\r\n										</div>\r\n									</div><p></p>', 9, 'en', '2024-06-08 13:34:19', '2024-06-08 13:34:19'),
